class BaseML_Lag_BC(RegressionPropsY, RegressionPropsVM):

    """
    ML estimation of the spatial lag model with Box-Cox transformations(note no consistency
    checks, diagnostics or constants added); Anselin (1988) [Anselin1988]_

    Parameters
    ----------
    y            : array
                   nx1 array for dependent variable
    x            : array
                   Two dimensional array with n rows and one column for each
                   independent (exogenous) variable, excluding the constant
    w            : pysal W object
                   Spatial weights object
    method       : string
                   if 'full', brute force calculation (full matrix expressions)
                   if 'ord', Ord eigenvalue method
                   if 'LU', LU sparse matrix decomposition
    epsilon      : float
                   tolerance criterion in mimimize_scalar function and inverse_product

    Attributes
    ----------
    betas        : array
                   (k+1)x1 array of estimated coefficients (rho first)
    rho          : float
                   estimate of spatial autoregressive coefficient
    lam          : float
                   estimate of Box-Cox transformation coefficient on dependent variable
    u            : array
                   nx1 array of residuals
    predy        : array
                   nx1 array of predicted y values
    n            : integer
                   Number of observations
    k            : integer
                   Number of variables for which coefficients are estimated
                   (including the constant, excluding the rho)
    y            : array
                   nx1 array for dependent variable
    x            : array
                   Two dimensional array with n rows and one column for each
                   independent (exogenous) variable, including the constant
    method       : string
                   log Jacobian method
                   if 'full': brute force (full matrix computations)
                   if 'ord' : Ord eigenvalue method
    epsilon      : float
                   tolerance criterion used in minimize_scalar function and inverse_product
    mean_y       : float
                   Mean of dependent variable
    std_y        : float
                   Standard deviation of dependent variable
    vm           : array
                   Variance covariance matrix (k+1 x k+1)
    vm1          : array
                   Variance covariance matrix (k+2 x k+2) includes sigma2
    sig2         : float
                   Sigma squared used in computations
    logll        : float
                   maximized log-likelihood (including constant terms)
    predy_e      : array
                   predicted values from reduced form
    e_pred       : array
                   prediction errors using reduced form predicted values

    """

    def __init__(self, y, x, w, method='full', epsilon=0.0000001):
        # set up main regression variables and spatial filters
        self.y = y
        self.x = x
        self.n, self.k = self.x.shape
        self.method = method
        self.epsilon = epsilon
        xtx = spdot(self.x.T, self.x)
        xtxi = la.inv(xtx)
        methodML = method.upper()
        # Set the initial parameter values for estimation
        x0 = [0.0, 0.5]
        # call minimizer using concentrated log-likelihood to get rho/lambda
        if methodML in ['FULL', 'LU', 'ORD']:
            if methodML == 'FULL':
                W = w.full()[0]     # moved here
                res = minimize(lag_c_loglik, x0, bounds=((-1.0, 1.0),(None, None)),
                                      args=(
                                          self.y, self.x, self.n, xtx, xtxi, w, W), method='L-BFGS-B',
                                      tol=epsilon)
            elif methodML == 'LU':
                I = sp.identity(w.n)
                Wsp = w.sparse  # moved here
                W = Wsp
                res = minimize(lag_c_loglik_sp, x0, bounds=((-1.0, 1.0),(None, None)),
                                      args=(self.n, e0, e1, I, Wsp),
                                      method='L-BFGS-B', tol=epsilon)
            elif methodML == 'ORD':
                # check on symmetry structure
                if w.asymmetry(intrinsic=False) == []:
                    ww = symmetrize(w)
                    WW = np.array(ww.todense())
                    evals = la.eigvalsh(WW)
                    W = WW
                else:
                    W = w.full()[0]     # moved here
                    evals = la.eigvals(W)
                res = minimize(lag_c_loglik_ord, x0, bounds=((-1.0, 1.0),(None, None)),
                                      args=(
                                          self.n, e0, e1, evals), method='L-BFGS-B',
                                      tol=epsilon)
        else:
            # program will crash, need to catch
            print(("{0} is an unsupported method".format(methodML)))
            self = None
            return

        self.rho = res.x[0]
        self.lam = res.x[1]

        # compute full log-likelihood, including constants
        ln2pi = np.log(2.0 * np.pi)
        llik = -res.fun - self.n / 2.0 * ln2pi - self.n / 2.0
        self.logll = llik[0][0]

        # b, residuals and predicted values
        ylag = ps.lag_spatial_bc(w, self.y, self.lam)
        xty = spdot(self.x.T, self.y)
        xtyl = spdot(self.x.T, ylag)
        b0 = np.dot(xtxi, xty)
        b1 = np.dot(xtxi, xtyl)
        e0 = y - spdot(self.x, b0)
        e1 = ylag - spdot(self.x, b1)

        b = b0 - self.rho * b1
        self.betas = np.vstack((b, self.rho, self.lam))   # rho and lambda added as last coefficients
        self.u = e0 - self.rho * e1
        predy = (self.y**self.lam-1)/self.lam - self.u
        # Convert predy in Box-Cox units to the original units of y
        self.predy = (np.abs((predy*self.lam)+1))**(1/self.lam)*np.sign((predy*self.lam)+1)

        xb = spdot(x, b)

        self.predy_e = inverse_prod(
            w.sparse, xb, self.rho, inv_method="power_exp", threshold=epsilon)
        self.e_pred = (self.y**self.lam-1)/self.lam - self.predy_e

        # residual variance
        self._cache = {}
        self.sig2 = self.sig2n  # no allowance for division by n-k

        # information matrix
        # if w should be kept sparse, how can we do the following:
        a = -self.rho * W
        spfill_diagonal(a, 1.0)
        ai = spinv(a)
        wai = spdot(W, ai)
        tr1 = wai.diagonal().sum() #same for sparse and dense

        wai2 = np.dot(wai, wai)
        tr2 = wai2.diagonal().sum()

        waiTwai = np.dot(wai.T, wai)
        tr3 = waiTwai.diagonal().sum()
        ### to here

        wpredy = ps.lag_spatial_bc(w, self.predy_e, self.lam)
        wpyTwpy = np.dot(wpredy.T, wpredy)
        xTwpy = spdot(x.T, wpredy)

        # Define the fixed values used in the calculation of var/covar for lambda
        # Fixed wrt lambda
        wtw = spdot(W.T, W)
        wx = spdot(W, self.x)
        wxb = spdot(wx, b)
        ylam = self.y**self.lam
        yty = ylam.T * ylam

        # Check for event participation
        lny = np.log(self.y)
        lny[lny == -1 * np.inf] = 0
        # Build components of lambda var/covar
        # d2F/dlam2
        a = (lny * self.lam * ylam - ylam + 1) / (2 * self.sig2 * self.lam**2)
        atw = spdot(a.T, wtw)
        atwa = spdot(atw,a)
        dadl = (self.lam**2 * lny**2 * ylam + 2 * ylam - 2 * self.lam * lny * ylam - 2) / (2 * self.sig2 * self.lam**3)
        atwdadl = spdot(atw, dadl)
        dads = -1 * a / self.sig2
        dbdl = (self.lam**2 * spdot((lny**2 * ylam).T, (2 * ylam - 1)) + 3 * spdot((ylam - 1).T, (ylam - 1)) - 4 * self.lam * spdot((lny * ylam).T, (ylam - 1))) \
                / (self.sig2 * self.lam**4)
        dbds = (-1 / self.sig2) * (spdot((ylam - 1).T, (self.lam * lny * ylam - ylam + 1)) / (self.sig2 * self.lam**3))
        c = spdot(dadl.T, W + 2 * self.rho * wtw)

        dFdll = -1 * dbdl + 4 * self.rho * spdot(c, a) + 2 * spdot(dadl.T, xb - self.rho * wxb)
        dFdlb = 2 * spdot(a.T, (self.x - self.rho * wx))
        dFdlr = 2 * atwa + 4 * self.rho *  - 2 * spdot(a.T, wxb)
        dFdls = dbds + 4 * self.rho * (atwdadl + atwa) + 2 * spdot(a.T, (self.rho * wxb - xb))

        # order of variables is beta, rho, lambda, sigma2

        v1 = np.vstack((xtx / self.sig2, xTwpy.T / self.sig2, dFdlb, np.zeros((1, self.k))))
        v2 = np.vstack((xTwpy / self.sig2, tr2 + tr3 + wpyTwpy / self.sig2, dFdlr, tr1 / self.sig2))
        v3 = np.vstack((dFdlb.T, dFdlr, dFdll, dFdls))
        v4 = np.vstack((np.zeros((self.k, 1)), tr1 / self.sig2, dFdls, self.n / (2.0 * self.sig2 ** 2)))

        v = np.hstack((v1, v2, v3, v4))

        self.vm1 = la.inv(v)  # vm1 includes variance for sigma2
        self.vm = self.vm1[:-1, :-1]  # vm is for coefficients only
        # CHECK
        self.vm[-1, -1] = np.abs(self.vm[-1, -1])
class ML_Lag_BC(BaseML_Lag_BC):

    """
    ML estimation of the spatial lag model with all results and diagnostics;
    Anselin (1988) [Anselin1988]_

    Parameters
    ----------
    y            : array
                   nx1 array for dependent variable
    x            : array
                   Two dimensional array with n rows and one column for each
                   independent (exogenous) variable, excluding the constant
    w            : pysal W object
                   Spatial weights object
    method       : string
                   if 'full', brute force calculation (full matrix expressions)
                   if 'ord', Ord eigenvalue method
    epsilon      : float
                   tolerance criterion in mimimize_scalar function and inverse_product
    spat_diag    : boolean
                   if True, include spatial diagnostics
    vm           : boolean
                   if True, include variance-covariance matrix in summary
                   results
    name_y       : string
                   Name of dependent variable for use in output
    name_x       : list of strings
                   Names of independent variables for use in output
    name_w       : string
                   Name of weights matrix for use in output
    name_ds      : string
                   Name of dataset for use in output

    Attributes
    ----------
    betas        : array
                   (k+1)x1 array of estimated coefficients (rho first)
    rho          : float
                   estimate of spatial autoregressive coefficient
    lam          : float
                   estimate of Box-Cox transformation coefficient on dependent variable
    u            : array
                   nx1 array of residuals
    predy        : array
                   nx1 array of predicted y values
    n            : integer
                   Number of observations
    k            : integer
                   Number of variables for which coefficients are estimated
                   (including the constant, excluding the rho)
    y            : array
                   nx1 array for dependent variable
    x            : array
                   Two dimensional array with n rows and one column for each
                   independent (exogenous) variable, including the constant
    method       : string
                   log Jacobian method
                   if 'full': brute force (full matrix computations)
    epsilon      : float
                   tolerance criterion used in minimize_scalar function and inverse_product
    mean_y       : float
                   Mean of dependent variable
    std_y        : float
                   Standard deviation of dependent variable
    vm           : array
                   Variance covariance matrix (k+1 x k+1), all coefficients
    vm1          : array
                   Variance covariance matrix (k+2 x k+2), includes sig2
    sig2         : float
                   Sigma squared used in computations
    logll        : float
                   maximized log-likelihood (including constant terms)
    aic          : float
                   Akaike information criterion
    schwarz      : float
                   Schwarz criterion
    predy_e      : array
                   predicted values from reduced form
    e_pred       : array
                   prediction errors using reduced form predicted values
    pr2          : float
                   Pseudo R squared (squared correlation between y and ypred)
    pr2_e        : float
                   Pseudo R squared (squared correlation between y and ypred_e
                   (using reduced form))
    utu          : float
                   Sum of squared residuals
    std_err      : array
                   1xk array of standard errors of the betas
    z_stat       : list of tuples
                   z statistic; each tuple contains the pair (statistic,
                   p-value), where each is a float
    name_y       : string
                   Name of dependent variable for use in output
    name_x       : list of strings
                   Names of independent variables for use in output
    name_w       : string
                   Name of weights matrix for use in output
    name_ds      : string
                   Name of dataset for use in output
    title        : string
                   Name of the regression method used

    """

    def __init__(self, y, x, w, method='full', epsilon=0.0000001,
                 spat_diag=False, vm=False, name_y=None, name_x=None,
                 name_w=None, name_ds=None):
        n = USER.check_arrays(y, x)
        USER.check_y(y, n)
        USER.check_weights(w, y, w_required=True)
        x_constant = USER.check_constant(x)
        method = method.upper()
        BaseML_Lag_BC.__init__(
            self, y=y, x=x_constant, w=w, method=method, epsilon=epsilon)
        # increase by 1 to have correct aic and sc, include rho and lambda in count
        self.k += 2
        self.title = "MAXIMUM LIKELIHOOD SPATIAL LAG WITH BOX-COX" + \
            " (METHOD = " + method + ")"
        self.name_ds = USER.set_name_ds(name_ds)
        self.name_y = USER.set_name_y(name_y)
        self.name_x = USER.set_name_x(name_x, x)
        name_ylag = USER.set_name_yend_sp(self.name_y)
        name_ybc = "LAMBDA"
        self.name_x.append(name_ylag)  # rho changed to last position
        self.name_x.append(name_ybc)
        self.name_w = USER.set_name_w(name_w, w)
        self.aic = DIAG.akaike(reg=self)
        self.schwarz = DIAG.schwarz(reg=self)
        SUMMARY.ML_Lag_BC(reg=self, w=w, vm=vm, spat_diag=spat_diag)

def lag_c_loglik(beta, y, x, n, xtx, xtxi, wps, W):
    # concentrated log-lik for lag model, no constants, brute force
    rho = beta[0]
    lam = beta[1]
    if beta[1] == 0:
        y = np.nan_to_num(np.log(y), copy=False)
    else:
        y = (y**lam - 1) / lam
    ylag = ps.lag_spatial(wps, y)
    xty = spdot(x.T, y)
    xtyl = spdot(x.T, ylag)
    b0 = np.dot(xtxi, xty)
    b1 = np.dot(xtxi, xtyl)
    e0 = y - spdot(x, b0)
    e1 = ylag - spdot(x, b1)
    er = e0 - rho * e1
    sig2 = np.dot(er.T, er) / n
    nlsig2 = (n / 2.0) * np.log(sig2)
    a = -rho * W
    spfill_diagonal(a, 1.0)
    jacob = np.log(np.linalg.det(a))
    # this is the negative of the concentrated log lik for minimization
    clik = nlsig2 - jacob
    return clik

def lag_c_loglik_sp(beta, n, e0, e1, I, Wsp):
    # concentrated log-lik for lag model, sparse algebra
    if isinstance(rho, np.ndarray):
        if beta[0].shape == (1,1):
            beta[0] = beta[0][0] #why does the interior value change?
    er = e0 - beta[0] * e1
    sig2 = np.dot(er.T, er) / n
    nlsig2 = (n / 2.0) * np.log(sig2)
    a = I - beta[0] * Wsp
    LU = SuperLU(a.tocsc())
    jacob = np.sum(np.log(np.abs(LU.U.diagonal())))
    clike = nlsig2 - jacob
    return clike

def lag_c_loglik_ord(beta, n, e0, e1, evals):
    # concentrated log-lik for lag model, no constants, Ord eigenvalue method
    er = e0 - beta[0] * e1
    sig2 = np.dot(er.T, er) / n
    nlsig2 = (n / 2.0) * np.log(sig2)
    revals = beta[0] * evals
    jacob = np.log(1 - revals).sum()
    if isinstance(jacob, complex):
        jacob = jacob.real
    # this is the negative of the concentrated log lik for minimization
    clik = nlsig2 - jacob
    return clik

